// Page-specific vendor initializations
const PageVendors = {
    init: function() {
        this.initializeChartPlugins();
        this.initializeDatepickers();
    },
    initializeChartPlugins: function() {
        // Initialize chart-specific plugins when available
        if (typeof Chart !== 'undefined') {
            Chart.defaults.global.defaultFontFamily = "'Inter', 'Helvetica', sans-serif";
            Chart.defaults.global.defaultFontSize = 13;
        }
    },
    initializeDatepickers: function() {
        if (typeof $.fn.datepicker !== 'undefined') {
            $('.datepicker').datepicker({
                autoclose: true,
                todayHighlight: true
            });
        }
    }
};

// Initialize when document is ready
document.addEventListener('DOMContentLoaded', function() {
    PageVendors.init();
});